import React from "react";
import {
  FormGroupContainer,
  FormInputContainer,
  FormInputLabel
} from "./form-input.style";

const Input = ({ handleChange, label, ...rest }) => {
  return (
    <FormGroupContainer>
      <FormInputContainer onChange={handleChange} {...rest} />
      {label ? (
        <FormInputLabel className={`${rest.value.length ? "shrink" : ""}`}>
          {label}
        </FormInputLabel>
      ) : null}
    </FormGroupContainer>
  );
};

export default Input;
