import styled from "styled-components";
import { color } from "assets/styles/variables";

export const CheckoutItemContainer = styled.div`
  width: 100%;
  display: flex;
  min-height: 10rem;
  border-bottom: 0.01rem solid $sub-color;
  padding: 1rem 0;
  align-items: center;
`;

export const ImageContainer = styled.div`
  width: 23.5%;
  padding-right: 10rem;

  img {
    width: 100%;
    height: 100%;
  }
`;

export const ImageItemContainer = styled.img`
  width: 100%;
  height: 100%;
`;
