import ActionType from "redux/user/user.types";

export const setCurrentUser = user => ({
  type: ActionType.SET_CURRENT_USER,
  payload: user
});

export const googleSignInStart = () => ({
  type: ActionType.GOOGLE_SIGN_IN_START
});

export const googleSignInSuccess = currentUser => ({
  type: ActionType.GOOGLE_SIGN_IN_SUCCESS,
  payload: currentUser
});

export const googleSignInFailure = errorMessage => ({
  type: ActionType.GOOGLE_SIGN_IN_FAILURE,
  payload: errorMessage
});

export const emailSignInStart = emailAndPassword => (
  console.log("emailP: ", emailAndPassword),
  {
    type: ActionType.EMAIL_SIGN_IN_START,
    payload: emailAndPassword
  }
);

export const emailSignInSuccess = user => ({
  type: ActionType.EMAIL_SIGN_IN_SUCCESS,
  payload: user
});

export const emailSignInFailure = errorMessage => ({
  type: ActionType.EMAIL_SIGN_IN_FAILURE,
  payload: errorMessage
});
