import { takeLatest, put, all, call } from "redux-saga/effects";
import ActionType from "redux/user/user.types";
import {
  signInSuccess,
  signInFailure,
  signOutFailure,
  signOutSuccess
} from "./user.action";
import {
  auth,
  googleProvider,
  createUserProfileDocument,
  getCurrentUser
} from "firebase/firebase.utils";

export function* createUserSnapshotFromUserAuth(userAuth) {
  try {
    const userRef = yield call(createUserProfileDocument, userAuth);
    const userSnapshot = yield userRef.get();
    yield put(signInSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
  } catch (err) {
    yield put(signInFailure(err));
  }
}

export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    yield createUserSnapshotFromUserAuth(user);
  } catch (err) {
    yield put(signInFailure(err));
  }
}

export function* signInWithEmail({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    yield createUserSnapshotFromUserAuth(user);
  } catch (err) {
    yield put(signInFailure(err));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(ActionType.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* onEmailSignInStart() {
  yield takeLatest(ActionType.EMAIL_SIGN_IN_START, signInWithEmail);
}

export function* isUserAuthenticated() {
  try {
    const UserAuth = yield getCurrentUser();
    if (!UserAuth) return;
    yield createUserSnapshotFromUserAuth(UserAuth);
  } catch (err) {
    yield put(signInFailure(err));
  }
}

export function* checkUserAuthentication() {
  yield takeLatest(ActionType.CHECK_USER_SESSION, isUserAuthenticated);
}

export function* signOut() {
  try {
    yield auth.signOut();
    yield put(signOutSuccess());
  } catch (err) {
    yield put(signOutFailure(err));
  }
}

export function* onSignOutStart() {
  yield takeLatest(ActionType.SIGN_OUT_START, signOut);
}

export function* signUpWithEmail() {
  console.log("Aa");
}

export function* onSignUpStart() {
  yield takeLatest(ActionType.SIGN_UP_START, signUpWithEmail);
}

export function* userSagas() {
  yield all([
    call(onGoogleSignInStart),
    call(onEmailSignInStart),
    call(checkUserAuthentication),
    call(onSignOutStart)
  ]);
}
