import { css } from "styled-components";

export const fontColor = {
  backgroundBlendPink: "pink",
  lightColor: "gainsboro",
  mainColor: "black",
  mainLightColor: "white",
  subColor: "grey",
  redColor: "red"
};

export const defaultFontSize = css`
  font-size: 1.6rem;
`;
