import { all, call } from "redux-saga/effects";
import { onfetchCollectionStart } from "redux/shop/shop.sagas";
import { onGoogleSignInStart } from "redux/shop/user.sagas";

export default function* rootSaga() {
  yield all([call(onfetchCollectionStart), call(onGoogleSignInStart)]);
}
