import { takeLatest, put, all, call } from "redux-saga/effects";
import userActionType from "redux/user/user.types";
import { clearCartItem, toggleCartHidden } from "./cart.action";

export function* clearCartOnSignOut() {
  yield put(clearCartItem());
  yield put(toggleCartHidden);
}

export function* onSignOutSuccess() {
  yield takeLatest(userActionType.SIGN_OUT_SUCCESS, clearCartOnSignOut);
}

export function* cartSagas() {
  yield all([call(onSignOutSuccess)]);
}
